import sys
import itertools

def read_input():
    """Reads input from standard input."""
    n, q = map(int, input().split())
    ranges = []
    for _ in range(q):
        l, r = map(int, input().split())
        ranges.append((l, r))
    return n, q, ranges

def calculate_intersection(ranges, i, j):
    """Calculates the intersection of two ranges."""
    l, r = ranges[i]
    li, ri = ranges[j]
    return (max(l, li), min(r, ri))

def calculate_union(ranges, i, j):
    """Calculates the union of two ranges."""
    l, r = ranges[i]
    li, ri = ranges[j]
    return (min(l, li), max(r, ri))

def get_covered_sections(n, ranges, selected_ranges):
    """Calculates the sections covered by the selected ranges."""
    covered_sections = set()
    for i, j in itertools.combinations(selected_ranges, 2):
        intersection = calculate_intersection(ranges, i, j)
        if intersection[0] <= intersection[1]:
            covered_sections.update(range(intersection[0], intersection[1] + 1))
    for i in selected_ranges:
        union = calculate_union(ranges, i, i)
        covered_sections.update(range(union[0], union[1] + 1))
    return covered_sections

def solve(n, q, ranges):
    """Solves the problem by trying all possible combinations of selected ranges."""
    max_covered_sections = 0
    for selected_ranges in itertools.combinations(range(q), q - 2):
        covered_sections = get_covered_sections(n, ranges, selected_ranges)
        max_covered_sections = max(max_covered_sections, len(covered_sections))
    return max_covered_sections

def main():
    """Main function."""
    n, q, ranges = read_input()
    max_covered_sections = solve(n, q, ranges)
    print(max_covered_sections)

