def is_s_palindrome(s):
    """
    Checks if a given string is an "s-palindrome".

    An "s-palindrome" is a string that is symmetric about its middle.

    Parameters:
    s (str): The input string.

    Returns:
    bool: True if the string is an "s-palindrome", False otherwise.
    """
    mid = len(s) // 2

    # Check if the string is odd or even length
    if len(s) % 2 == 0:
        # If even length, compare characters from both ends
        for i in range(mid):
            if s[i] != s[-i - 1]:
                return False
    else:
        # If odd length, compare characters from both ends excluding the middle character
        for i in range(mid):
            if s[i] != s[-i - 1]:
                return False

    return True

