import sys

def min_operations(n, s):
    dp = [[0] * (n + 1) for _ in range(n + 1)]

    for i in range(1, n + 1):
        for j in range(1, n + 1):
            if j == 1:
                dp[i][j] = i
            elif i == j:
                dp[i][j] = j
            elif s[i - 1] == s[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = min(dp[i - 1][j], dp[i][j - 1]) + 1

    lcs = dp[n][n]
    return n - lcs + 2 if lcs > 1 else n

