import math

def egcd(a, b):
    """return (g, x, y) such that a*x + b*y = g = gcd(a, b)"""
    if a == 0:
        return (b, 0, 1)
    else:
        g, y, x = egcd(b % a, a)
        return (g, x - (b // a) * y, y)

def modinv(a, m):
    """return inverse of a mod m"""
    g, x, y = egcd(a, m)
    if g != 1:
        raise Exception('modular inverse does not exist')
    else:
        return x % m

def solve(m, x):
    """Return minimum number of traps needed to catch the x-mouse"""
    # Find smallest y such that x^y % m = 1
    y = 1
    while True:
        if pow(x, y, m) == 1:
            break
        y += 1

    # Calculate number of distinct cycles
    num_cycles = m // math.gcd(y, m)
    return num_cycles

