def count_prefixes(n, x, s):
    zeros = s.count('0')
    ones = n - zeros

    if zeros - ones == x:
        return n

    prefix_count = 0
    i = 0
    while i < n:
        if zeros - ones == x:
            prefix_count += n - i
            break

        if s[i] == '0':
            zeros -= 1
        else:
            ones -= 1

        if zeros - ones == x:
            prefix_count += n - i
            break

        i += 1

    if zeros - ones == x:
        return prefix_count

    j = 0
    k = 0
    while k < n:
        if zeros - ones == x:
            break

        if s[j] == '0':
            zeros -= 1
        else:
            ones -= 1

        if zeros - ones == x:
            prefix_count += (n - k) // (n - j)
            break

        j += 1
        k += 1

    if zeros - ones == x:
        return prefix_count

    return -1


