import re
from collections import defaultdict

def calculate_beauty(ribbon):
    max_count = 0
    for match in re.finditer(r"(.+?)\1*", ribbon):
        count = sum(1 for _ in re.finditer(r"\1+", match.group()))
        max_count = max(max_count, count)
    return max_count

def get_max_beauty(ribbons, n):
    max_beauty = 0
    for i in range(len(ribbons)):
        beauty = calculate_beauty(ribbons[i])
        if beauty + (n - 1) * (len(ribbons[0]) - len(re.findall(r"(.+?)\1*", ribbons[i])[0])) >= max_beauty:
            max_beauty = beauty + (n - 1) * (len(ribbons[0]) - len(re.findall(r"(.+?)\1*", ribbons[i])[0]))
    return max_beauty

def find_winner(n, ribbons):
    max_beauty = 0
    for i in range(len(ribbons)):
        beauty = get_max_beauty(ribbons, n)
        if beauty > max_beauty:
            max_beauty = beauty
            winner = i
    if max_beauty == get_max_beauty(ribbons, n) * 3:
        return "Draw"
    if max_beauty == get_max_beauty([ribbons[winner]], n) * 3:
        return ["Kuro", "Shiro", "Katie"][winner]
    else:
        return ["Kuro", "Shiro", "Katie"][winner]

