def find_sequence(n, k):
    # Find the maximum possible value for y
    y = 0
    while (1 << (y + 1)) <= n:
        y += 1
    y = min(y, k)

    # If it's not possible to find a sequence with the minimum y value
    if (1 << y) > n:
