    def largestValsFromLabels(self, values: List[int], labels: List[int], num_wanted: int, use_limit: int) -> int:
        # Create a dictionary to store the items for each label
        l2v = collections.defaultdict(list)
        for v, l in zip(values, labels):
            l2v[l].append(v)

        # Create a pool of items, with the most valuable items for each label first
        pool = []
        for l in l2v:
            pool += sorted(l2v[l])[-use_limit:]

        # Return the sum of the num_wanted most valuable items in the pool
