    for c in s:
        if c == '[':
            stack.append(c)
        elif c == ']':
            if not stack or stack[-1] != '[':
                return -1
            stack.pop()
        elif c == ':':
            if colon == 2 or (len(stack) > 0 and stack[-1] == ':'):
                return -1
            if len(stack) == 0:
                colon += 1
            else:
                if stack[-1] == ':':
                    return -1
                stack.append(':')
        elif c == '|':
            if len(stack) == 0 or stack[-1] != ':':
                return -1
            stack.append('|')
        else:
            return -1
    if len(stack) != 0 or colon != 1:
        return -1
    return len(s) - len(stack)

