        dummy.next = head
        p1 = p2 = dummy
        for _ in range(n):
            p1 = p1.next
        while p1.next:
            p1 = p1.next
            p2 = p2.next
        p2.next = p2.next.next
        return dummy.next

def binary_simulation(s, q):
    out, n, s = [], int(s, 2), len(s)
    for cmd, *i in q:
        if cmd == 'I':
            a, b = i
            n ^= (1 << b - a + 1) - 1 << s - b
        else:
            out.append(str(int(0 < 1 << s - i[0] & n)))
    return out

def binarr(a, k, s):
    a.sort(reverse=True)
    arr = [0]*k
    for i in range(k):
        arr[i] = a[i]
    if sum(arr) <= s:
        return binarr(a, k+1, s)
    return len(arr)

