def f(m):
    # Base case: if m is 1, the expected length is 1
    if m == 1:
        return 1

    # Recursive case: compute the expected length for m-1
    # and add the probability that the GCD is 1
    return (m - 1 + f(m - 1)) * pow(m - 1, -1, 10**9 + 7)

