def is_unimodal(arr):
    increasing = True
    constant = False
    for i in range(1, len(arr)):
        if arr[i] > arr[i - 1]:
            if constant:
                return "NO"
            increasing = True
        elif arr[i] < arr[i - 1]:
            increasing = False
            constant = False
        else:
            constant = True
    if increasing:
        return "YES"
    elif not constant:
        return "NO"
    else:
        return "YES"

n = int(input())
arr = list(map(int, input().split()))
print(is_unimodal(arr))

import math

def path(x):
    res = []
    while x > 1:
        res.append(x)
        if x % 2 == 0:
            x = x // 2
        else:
            x = x - 1
    return res[::-1]

def max_value(n, k):
    for y in range(n, 0, -1):
        count = 0
        for x in range(1, n + 1):
            if y in path(x):
                count += 1
            if count >= k:
                return y
    return 1

n, k = map(int, input().split())
print(max_value(n, k))

